func generateDropperV2CutFacetDeployBytecode() ([]byte, error) {
	abiPacked, err := DropperV2CutFacetMetaData.GetAbi()
	if err != nil {
		return nil, fmt.Errorf("failed to get ABI: %v", err)
	}

	constructorArguments, err := abiPacked.Pack("")
	if err != nil {
		return nil, fmt.Errorf("failed to pack constructor arguments: %v", err)
	}

	deployBytecode := append(common.FromHex(DropperV2CutFacetMetaData.Bin), constructorArguments...)
	return deployBytecode, nil
}

