func generateDropperV3FacetDeployBytecode() ([]byte, error) {
	abiPacked, err := DropperV3FacetMetaData.GetAbi()
	if err != nil {
		return nil, fmt.Errorf("failed to get ABI: %v", err)
	}

	constructorArguments, err := abiPacked.Pack("")
	if err != nil {
		return nil, fmt.Errorf("failed to pack constructor arguments: %v", err)
	}

	deployBytecode := append(common.FromHex(DropperV3FacetMetaData.Bin), constructorArguments...)
	return deployBytecode, nil
}

