func generateTokenSenderDeployBytecode(
	faucetTimeInterval *big.Int,
) ([]byte, error) {
	abiPacked, err := TokenSenderMetaData.GetAbi()
	if err != nil {
		return nil, fmt.Errorf("failed to get ABI: %v", err)
	}

	constructorArguments, err := abiPacked.Pack("",
		faucetTimeInterval,
	)
	if err != nil {
		return nil, fmt.Errorf("failed to pack constructor arguments: %v", err)
	}

	deployBytecode := append(common.FromHex(TokenSenderMetaData.Bin), constructorArguments...)
	return deployBytecode, nil
}

