func generateDiamondLoupeFacetDeployBytecode() ([]byte, error) {
	abiPacked, err := DiamondLoupeFacetMetaData.GetAbi()
	if err != nil {
		return nil, fmt.Errorf("failed to get ABI: %v", err)
	}

	constructorArguments, err := abiPacked.Pack("")
	if err != nil {
		return nil, fmt.Errorf("failed to pack constructor arguments: %v", err)
	}

	deployBytecode := append(common.FromHex(DiamondLoupeFacetMetaData.Bin), constructorArguments...)
	return deployBytecode, nil
}

